  OracleCommand cmd;  
  
  // Create the SelectCommand.  
  
  cmd = new OracleCommand("SELECT * FROM Dept " +  
                       "WHERE DName = :pDName AND Loc = :pLoc", conn);  
  
  cmd.Parameters.Add("pDName", OracleType.NVarChar, 14);  
  cmd.Parameters.Add("pLoc", OracleType.NVarChar, 13);  
  
  da.SelectCommand = cmd;  
  
  // Create the InsertCommand.  
  
  cmd = new OracleCommand("INSERT INTO Dept (DeptNo, DName) " +  
                       "VALUES (:pDeptNo, :pDName)", conn);  
  
  cmd.Parameters.Add("pDeptNo", OracleType.Number, 2, "DeptNo");  
  cmd.Parameters.Add("pDName", OracleType.NVarChar, 14, "DName");  
  
  da.InsertCommand = cmd;  
  
  return da;  
}  
```

  
  
## Remarks  
 <span data-ttu-id="fd5c9-200">當<xref:System.Data.OracleClient.OracleDataAdapter.SelectCommand%2A>指派至先前建立<xref:System.Data.OracleClient.OracleCommand>，則<xref:System.Data.OracleClient.OracleCommand>不複製。</span><span class="sxs-lookup"><span data-stu-id="fd5c9-200">When <xref:System.Data.OracleClient.OracleDataAdapter.SelectCommand%2A> is assigned to a previously created <xref:System.Data.OracleClient.OracleCommand>, the <xref:System.Data.OracleClient.OracleCommand> is not cloned.</span></span> <span data-ttu-id="fd5c9-201">相反地，<xref:System.Data.OracleClient.OracleDataAdapter.SelectCommand%2A>維護先前建立的參考<xref:System.Data.OracleClient.OracleCommand>物件。</span><span class="sxs-lookup"><span data-stu-id="fd5c9-201">Instead, the <xref:System.Data.OracleClient.OracleDataAdapter.SelectCommand%2A> maintains a reference to the previously created <xref:System.Data.OracleClient.OracleCommand> object.</span></span>  
  
 <span data-ttu-id="fd5c9-202">如果<xref:System.Data.OracleClient.OracleDataAdapter.SelectCommand%2A>不會傳回任何資料列，任何資料表會加入至**資料集**，並會引發任何例外狀況。</span><span class="sxs-lookup"><span data-stu-id="fd5c9-202">If <xref:System.Data.OracleClient.OracleDataAdapter.SelectCommand%2A> does not return any rows, no tables are added to the **DataSet**, and no exception is raised.</span></span>  
  
   
  
## Examples  
 <span data-ttu-id="fd5c9-203">下列範例會建立<xref:System.Data.OracleClient.OracleDataAdapter>，並設定<xref:System.Data.OracleClient.OracleDataAdapter.SelectCommand%2A>和<xref:System.Data.OracleClient.OracleDataAdapter.InsertCommand%2A>屬性。</span><span class="sxs-lookup"><span data-stu-id="fd5c9-203">The following example creates an <xref:System.Data.OracleClient.OracleDataAdapter> and sets the <xref:System.Data.OracleClient.OracleDataAdapter.SelectCommand%2A> and <xref:System.Data.OracleClient.OracleDataAdapter.InsertCommand%2A> properties.</span></span> <span data-ttu-id="fd5c9-204">它會假設您已經建立<xref:System.Data.OracleClient.OracleConnection>物件。</span><span class="sxs-lookup"><span data-stu-id="fd5c9-204">It assumes you have already created an <xref:System.Data.OracleClient.OracleConnection> object.</span></span>  
  
```vb  
Public Shared Function CreateCustomerAdapter(conn As OracleConnection) As OracleDataAdapter   
  
  Dim da As OracleDataAdapter = New OracleDataAdapter()  
  Dim cmd As OracleCommand  
  
  ' Create the SelectCommand.  
  
  cmd = New OracleCommand("SELECT * FROM Dept " & _  
                       "WHERE DName = :pDName AND Loc = :pLoc", conn)  
  
  cmd.Parameters.Add("pDName", OracleType.NVarChar, 14)  
  cmd.Parameters.Add("pLoc", OracleType.NVarChar, 13)  
  
  da.SelectCommand = cmd  
  
  ' Create the InsertCommand.  
  
  cmd = New OracleCommand("INSERT INTO Dept (DeptNo, DName) " & _  
                       "VALUES (pDeptNo, pDName)", conn)  
  
  cmd.Parameters.Add("pDeptNo", OracleType.Number, 2, "DeptNo")  
  cmd.Parameters.Add("pDName", OracleType.NVarChar, 14, "DName")  
  
  da.InsertCommand = cmd  
  
  Return da  
End Function  
```  
  
```csharp  
public static OracleDataAdapter CreateCustomerAdapter(OracleConnection conn)  
{  
  OracleDataAdapter da = new OracleDataAdapter();  
  OracleCommand cmd;  
  
  // Create the SelectCommand.  
  
  cmd = new OracleCommand("SELECT * FROM Dept " +  
                       "WHERE DName = :pDName AND Loc = :pLoc", conn);  
  
  cmd.Parameters.Add("pDName", OracleType.NVarChar, 14);  
  cmd.Parameters.Add("pLoc", OracleType.NVarChar, 13);  
  
  da.SelectCommand = cmd;  
  
  // Create the InsertCommand.  
  
  cmd = new OracleCommand("INSERT INTO Dept (DeptNo, DName) " +  
                       "VALUES (:pDeptNo, :pDName)", conn);  
  
  cmd.Parameters.Add("pDeptNo", OracleType.Number, 2, "DeptNo");  
  cmd.Parameters.Add("pDName", OracleType.NVarChar, 14, "DName");  
  
  da.InsertCommand = cmd;  
  
  return da;  
}  
```

  
  
## Remarks  
 <span data-ttu-id="fd5c9-236">當<xref:System.Data.OracleClient.OracleDataAdapter.UpdateCommand%2A>指派至先前建立<xref:System.Data.OracleClient.OracleCommand>，則<xref:System.Data.OracleClient.OracleCommand>不複製。</span><span class="sxs-lookup"><span data-stu-id="fd5c9-236">When <xref:System.Data.OracleClient.OracleDataAdapter.UpdateCommand%2A> is assigned to a previously created <xref:System.Data.OracleClient.OracleCommand>, the <xref:System.Data.OracleClient.OracleCommand> is not cloned.</span></span> <span data-ttu-id="fd5c9-237">相反地，<xref:System.Data.OracleClient.OracleDataAdapter.UpdateCommand%2A>維護先前建立的參考<xref:System.Data.OracleClient.OracleCommand>物件。</span><span class="sxs-lookup"><span data-stu-id="fd5c9-237">Instead, the <xref:System.Data.OracleClient.OracleDataAdapter.UpdateCommand%2A> maintains a reference to the previously created <xref:System.Data.OracleClient.OracleCommand> object.</span></span>  
  
 <span data-ttu-id="fd5c9-238">更新作業期間，如果<xref:System.Data.OracleClient.OracleDataAdapter.InsertCommand%2A>集和主索引鍵不是資訊必須位於**資料集**，您可以使用<xref:System.Data.OracleClient.OracleCommandBuilder>類別，以自動產生<xref:System.Data.OracleClient.OracleDataAdapter.InsertCommand%2A>，和其他命令所需協調<xref:System.Data.DataSet>到資料庫。</span><span class="sxs-lookup"><span data-stu-id="fd5c9-238">During an update operation, if <xref:System.Data.OracleClient.OracleDataAdapter.InsertCommand%2A> is not set and primary key information is present in the **DataSet**, you can use the <xref:System.Data.OracleClient.OracleCommandBuilder> class to automatically generate <xref:System.Data.OracleClient.OracleDataAdapter.InsertCommand%2A>, and additional commands needed to reconcile the <xref:System.Data.DataSet> to the database.</span></span> <span data-ttu-id="fd5c9-239">若要這樣做，請設定<xref:System.Data.OracleClient.OracleDataAdapter.SelectCommand%2A>屬性<xref:System.Data.OracleClient.OracleDataAdapter>。</span><span class="sxs-lookup"><span data-stu-id="fd5c9-239">To do this, set the <xref:System.Data.OracleClient.OracleDataAdapter.SelectCommand%2A> property of the <xref:System.Data.OracleClient.OracleDataAdapter>.</span></span> <span data-ttu-id="fd5c9-240">產生邏輯也需要索引鍵資料行資訊會出現在<xref:System.Data.DataSet>。</span><span class="sxs-lookup"><span data-stu-id="fd5c9-240">The generation logic also requires key column information to be present in the <xref:System.Data.DataSet>.</span></span> <span data-ttu-id="fd5c9-241">如需詳細資訊，請參閱[Commandbuilder 產生命令](~/docs/framework/data/adonet/generating-commands-with-commandbuilders.md)。</span><span class="sxs-lookup"><span data-stu-id="fd5c9-241">For more information see [Generating Commands with CommandBuilders](~/docs/framework/data/adonet/generating-commands-with-commandbuilders.md).</span></span>  
  
> [!NOTE]
>  <span data-ttu-id="fd5c9-242">如果執行此命令會傳回資料列，可能與合併這些資料列<xref:System.Data.DataSet>取決於您的設定方式而定<xref:System.Data.OracleClient.OracleCommand.UpdatedRowSource%2A>屬性<xref:System.Data.OracleClient.OracleCommand>物件。</span><span class="sxs-lookup"><span data-stu-id="fd5c9-242">If execution of this command returns rows, these rows may be merged with the <xref:System.Data.DataSet> depending upon how you set the <xref:System.Data.OracleClient.OracleCommand.UpdatedRowSource%2A> property of the <xref:System.Data.OracleClient.OracleCommand> object.</span></span>  
  
 <span data-ttu-id="fd5c9-243">當您更新的資料行`LONG RAW`資料類型，會擲回例外狀況時輸入值為`NULL`資料行中。</span><span class="sxs-lookup"><span data-stu-id="fd5c9-243">When you update a column with the `LONG RAW` data type, an exception is thrown when you enter a value of `NULL` in the column.</span></span> <span data-ttu-id="fd5c9-244">Oracle`LONG RAW`資料類型是已被取代的類型在 Oracle 8.0 版。</span><span class="sxs-lookup"><span data-stu-id="fd5c9-244">The Oracle `LONG RAW` data type is a deprecated type in Oracle version 8.0.</span></span> <span data-ttu-id="fd5c9-245">若要避免這個錯誤，請使用`BLOB`資料類型，而非`LONG RAW`。</span><span class="sxs-lookup"><span data-stu-id="fd5c9-245">To avoid this error, use the `BLOB` data type instead of `LONG RAW`.</span></span>  
  
   
  
## Examples  
 <span data-ttu-id="fd5c9-246">下列範例會建立<xref:System.Data.OracleClient.OracleDataAdapter>，並設定<xref:System.Data.OracleClient.OracleDataAdapter.SelectCommand%2A>和<xref:System.Data.OracleClient.OracleDataAdapter.UpdateCommand%2A>屬性。</span><span class="sxs-lookup"><span data-stu-id="fd5c9-246">The following example creates an <xref:System.Data.OracleClient.OracleDataAdapter> and sets the <xref:System.Data.OracleClient.OracleDataAdapter.SelectCommand%2A> and <xref:System.Data.OracleClient.OracleDataAdapter.UpdateCommand%2A> properties.</span></span> <span data-ttu-id="fd5c9-247">它會假設您已經建立<xref:System.Data.OracleClient.OracleConnection>物件。</span><span class="sxs-lookup"><span data-stu-id="fd5c9-247">It assumes you have already created an <xref:System.Data.OracleClient.OracleConnection> object.</span></span>  
  
```vb  
Public Shared Function CreateCustomerAdapter(conn As OracleConnection) As OracleDataAdapter   
  
  Dim da As OracleDataAdapter = New OracleDataAdapter()  
  Dim cmd As OracleCommand  
  Dim parm As OracleParameter  
  
  ' Create the SelectCommand.  
  
  cmd = New OracleCommand("SELECT * FROM Dept " & _  
                       "WHERE DName = pDName AND Loc = pLoc", conn)  
  
  cmd.Parameters.Add("pDName", OracleType.NVarChar, 14)  
  cmd.Parameters.Add("pLoc", OracleType.NVarChar, 13)  
  
  da.SelectCommand = cmd  
  
  ' Create the UpdateCommand.  
  
  cmd = New OracleCommand("UPDATE Dept SET DeptNo = pDeptNo, DName = pDName " & _  
                       "WHERE DeptNo = poldDeptNo", conn)  
  
  cmd.Parameters.Add("pDeptNo", OracleType.Number, 2, "DeptNo")  
  cmd.Parameters.Add("pDName", OracleType.NVarChar, 14, "DName")  
  
  parm = cmd.Parameters.Add("poldDeptNo", OracleType.Number, 2, "DeptNo")  
  parm.SourceVersion = DataRowVersion.Original  
  
  da.UpdateCommand = cmd  
  
  Return da  
End Function  
```  
  
```csharp  
public static OracleDataAdapter CreateCustomerAdapter(OracleConnection conn)  
{  
  OracleDataAdapter da = new OracleDataAdapter();  
  OracleCommand cmd;  
  OracleParameter parm;  
  
  // Create the SelectCommand.  
  
  cmd = new OracleCommand("SELECT * FROM Dept " +  
                       "WHERE DName = pDName AND Loc = pLoc", conn);  
  
  cmd.Parameters.Add("pDName", OracleType.NVarChar, 14);  
  cmd.Parameters.Add("pLoc", OracleType.NVarChar, 13);  
  
  da.SelectCommand = cmd;  
  
  // Create the UpdateCommand.  
  
  cmd = new OracleCommand("UPDATE Dept SET DeptNo = pDeptNo, DName = pDName " +  
                       "WHERE DeptNo = poldDeptNo", conn);  
  
  cmd.Parameters.Add("pDeptNo", OracleType.Number, 2, "DeptNo");  
  cmd.Parameters.Add("pDName", OracleType.NVarChar, 14, "DName");  
  
  parm = cmd.Parameters.Add("poldDeptNo", OracleType.Number, 2, "DeptNo");  
  parm.SourceVersion = DataRowVersion.Original;  
  
  da.UpdateCommand = cmd;  
  
  return da;  
}  
```
